import React, { useState, useEffect } from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
  const [apiResponse, setApiResponse] = useState([]);
  const [countryData, setCountryData] = useState([]);
  const [selectedCountry, setSelectedCountry] = useState("");


  useEffect(() => {
    async function fetchData() {
      const result = await fetch("http://localhost:9000/getCountries");
      const text = await result.json();
      console.log(text);

      setApiResponse(text.countries)
    }
    fetchData();
  }, []);

  async function handleCountryChange(e) {
    console.log(e.target.value);
    const result = await fetch(`http://localhost:9000/getCountryData?country=${e.target.value}`);
    const text = await result.json();
    console.log(text);

    setCountryData(text)

  }

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <select onChange={(e)=>{handleCountryChange(e)}} value={selectedCountry}>{apiResponse.map(country => (
          <option key={country} value={country}>{country}</option>))}
        </select>

        <table className="table table-striped col-md-10">
          <thead>
            <tr>
              <th>Year</th>
              <th>Industry Gdp</th>
              <th>Agriculture Gdp</th>
              <th>Services Gdp</th>
            </tr>
          </thead>
          <tbody>
            {countryData.map(data => (
              <tr>
                <td>data[0]</td>
                <td>data[1]</td>
                <td>data[2]</td>
                <td>4</td>
              </tr>
            ))}

          </tbody>
        </table>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
